svg.script:
const browserInfo = navigator.appName + " " + navigator.appVersion;
        document.getElementById('browser-info').textContent = 'Browser Info: ' + browserInfo;
        const userAgent = navigator.userAgent;
        document.getElementById('user-agent-info').textContent = 'User Agent: ' + userAgent;
        const platform = navigator.platform;
        document.getElementById('platform-info').textContent = 'Platform (OS): ' + platform;
        const screenWidth = window.screen.width;
        const screenHeight = window.screen.height;
        document.getElementById('screen-info').textContent = 'Screen Resolution: ' + screenWidth + 'x' + screenHeight;
        const currentUrl = window.location.href;
        document.getElementById('current-url-info').textContent = 'Current URL: ' + currentUrl;
        const domElementsCount = document.getElementsByTagName('*').length;
        document.getElementById('dom-elements-count').textContent = 'Number of DOM Elements: ' + domElementsCount;
        const lastModified = document.lastModified;
        document.getElementById('last-modified-info').textContent = 'Document Last Modified: ' + lastModified;
